import { Component } from '@angular/core';
import { FormGroup,FormBuilder,Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import {Login} from 'src/app/models/login';
import { LoginService } from 'src/app/services/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup
  titulo=" Login"
  id:string | null;

  constructor(private fb: FormBuilder, 
    private router: Router,
    private toastr: ToastrService,
    private _loginService:LoginService,
    private arouter: ActivatedRoute
    ){
    this.loginForm = this.fb.group({
      nombre: ['', Validators.required],
      apellido: ['', Validators.required],
      usuario: ['', Validators.required],
      contrasena: ['', Validators.required]
    } )
    this.id = this.arouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void{

  }

  agregarUsuario(){
    const login: Login= {
      nombre: this.loginForm.get('nombre')?.value,
      apellido: this.loginForm.get('apellido')?.value,
      usuario: this.loginForm.get('usuario')?.value,
      contrasena: this.loginForm.get('contrasena')?.value
    }

    if(this.id !== null){
      //editar un producto
      this._loginService.editarUsuario(this.id,login).subscribe({
        next: data => {
          this.toastr.info('El producto fue actualizado con exito!', 'Usuario Actualizado!');
          this.router.navigate(['/']);
          this.loginForm.reset();
        },
        error: error => {
          console.log(error);
          this.loginForm.reset();
        }
      });
    }else{
      //creamos el login
      console.log(login)
      this._loginService.obtenerUsuario(login).subscribe({
        next: data => {
          this.toastr.success('Ingreso exitosamente', 'Ingreso correcto!');
          this.router.navigate(['/producto']);
        },
        error: error => {
          console.log(error);
          this.loginForm.reset();
        }
      });
    }


  }

}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-6 offset-lg-2">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">
                    </span>
                    <form class="mt-3" [formGroup]="loginForm" (ngSubmit)="agregarUsuario() ">
                        <div class="mb-3">
                            <label class="float-start mb-2">Nombre:</label>
                           <input type="text" 
                            formControlName="nombre"
                            class="form-control form-control-lm" 
                            placeholder="Nombre">
                            <div class="text-danger" 
                            *ngIf="loginForm.get('nombre')?.hasError('required') && loginForm.get('nombre')?.touched ">
                                 <span>El nombre del usuario  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <label class="float-start mb-2">Apellido:</label>
                           <input type="text" 
                            formControlName="apellido"
                            class="form-control form-control-lm" 
                            placeholder="Apellido">
                            <div class="text-danger" 
                            *ngIf="loginForm.get('apellido')?.hasError('required') && loginForm.get('apellido')?.touched ">
                                 <span>El nombre del usuario  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <label class="float-start mb-2">Usuario:</label>
                           <input type="text" 
                            formControlName="usuario"
                            class="form-control form-control-lm" 
                            placeholder="Usuario">
                            <div class="text-danger" 
                            *ngIf="loginForm.get('usuario')?.hasError('required') && loginForm.get('usuario')?.touched ">
                                 <span>El nombre del usuario  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <label class="float-start mb-2">Contraseña:</label>
                            <input type="text" 
                            formControlName="contrasena"
                            class="form-control form-control-lm" 
                            placeholder="Categoria">
                            <div class="text-danger"
                            *ngIf="loginForm.get('contrasena')?.hasError('required') && loginForm.get('contrasena')?.touched ">
                                <span>La contraseña  <strong>es obligatorio</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <button routerLink="/crear-usuario" class="btn btn-secondary btn-lg float-start">Crear usuario</button>
                            <button [disabled]="loginForm.invalid" [routerLink]="['/',]" type="submit"  class="btn btn-success  btn-lg float-end">Ingresar</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
