import { Component,OnInit } from '@angular/core';
import { Login } from 'src/app/models/login';
import { LoginService } from 'src/app/services/login.service';
import { FormGroup,FormBuilder,Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  loginForm: FormGroup
  id:string | null;

  listUsuario: Login[] =[];
  constructor(
    private _loginService: LoginService,
    private toastr:ToastrService,
    private fb: FormBuilder, 
    private router: Router,
    private arouter: ActivatedRoute
    ){
      this.loginForm = this.fb.group({
        usuario: ['', Validators.required],
        contrasena: ['', Validators.required]
      } )
      this.id = this.arouter.snapshot.paramMap.get('id');
    }

  ngOnInit(): void{
    this.obtenerUsuario();
  }
  obtenerUsuario() {
    this._loginService.getLogin().subscribe({
      next: data => {
        console.log(data);
        this.listUsuario =data;
      },
      error: error => {
        console.log(error);
      }
    });
  }
  obtenerUsuarios(id: any){
    this._loginService.obtenerUsuario(id).subscribe({
      next: data => {
        this.toastr.error('El producto fue eliminado con exito','Producto eliminado')
        this.obtenerUsuario();
      },
      error: error => {
        console.log(error);
      }
    });
  }
}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-6 offset-lg-2">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">
                    </span>
                    <form class="mt-3" [formGroup]="loginForm" (ngSubmit)="obtenerUsuario() ">
                        <div class="mb-3">
                            <label class="float-start mb-2">Usuario:</label>
                           <input type="text" 
                            formControlName="usuario"
                            class="form-control form-control-lm" 
                            placeholder="Usuario">
                            <div class="text-danger" *ngIf="loginForm.get('producto')?.hasError('required') && productoForm.get('producto')?.touched ">
                                 <span>El nombre del usuario  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <label class="float-start mb-2">Contraseña:</label>
                            <input type="text" 
                            formControlName="categoria"
                            class="form-control form-control-lm" 
                            placeholder="Categoria">
                            <div class="text-danger">
                                <span>La contraseña  <strong>es obligatorio</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <button routerLink="/" class="btn btn-secondary btn-lg float-start">Volver</button>
                            <button [routerLink]="['/login',]" type="submit"  class="btn btn-success  btn-lg float-end">Ingresar</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
