import { Target } from '@angular/compiler';
import { Component } from '@angular/core';
import { FormGroup,FormBuilder,Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import {Empleado} from 'src/app/models/empleado';
import { EmpleadoService } from 'src/app/services/empleado.service';
@Component({
  selector: 'app-crear-empleado',
  templateUrl: './crear-empleado.component.html',
  styleUrls: ['./crear-empleado.component.css']
})
export class CrearEmpleadoComponent {
  empleadoForm: FormGroup
  titulo=" Crear Pago Empleado"
  id:string | null;
  kilosCont:String | null;
  totalKilos: number = 0;

  constructor(private fb: FormBuilder, 
    private router: Router,
    private toastr: ToastrService,
    private _empleadoService:EmpleadoService,
    private arouter: ActivatedRoute
    ){
    this.empleadoForm = this.fb.group({
      nombre: ['', Validators.required],
      apellido: ['', Validators.required],
      kilos: [null, Validators.required],
      descuentos: ['', Validators.required],
      pago: ['', Validators.required]
    } )
    this.id = this.arouter.snapshot.paramMap.get('id');
    this.kilosCont="";
  }

  ngOnInit(): void{
    this.esEditar();
  }
  array: any[] = [];
  addValue(event: any) {       
    event.preventDefault();
    this.array.push(this.empleadoForm.get('kilos')?.value);
    this.empleadoForm.get('kilos')?.reset();
    console.log(this.array)
  }
  onInput(event: Target) {
    // Puedes hacer lo que necesites con el valor del input aquí
    const sum = this.array.reduce((accumulator, currentValue) => accumulator + currentValue, 0);
    this.totalKilos = sum;
    console.log("suma de array"+ this.totalKilos)
    return sum;
  }
  agregarEmpleado(){
    const empleado: Empleado= {
      nombre: this.empleadoForm.get('nombre')?.value,
      apellido: this.empleadoForm.get('apellido')?.value,
      kilos: this.totalKilos,
      descuentos:this.empleadoForm.get('descuentos')?.value,
      pago:this.empleadoForm.get('pago')?.value
    }


    console.log(this.array)
    if(this.id !== null){
      //editar un empleado
      this._empleadoService.editarEmpleado(this.id,empleado).subscribe({
        next: data => {
          this.toastr.info('El empleado fue actualizado con exito!', 'Empleado Actualizado!');
          this.router.navigate(['/']);
          this.empleadoForm.reset();
        },
        error: error => {
          console.log(error);
          this.empleadoForm.reset();
        }
      });
    }else{
      //creamos el empleado
      console.log(empleado)
      this._empleadoService.guardarEmpleado(empleado).subscribe({
        next: data => {
          this.toastr.success('El empleado fue registrado con exito!', 'Empleado Registrado!');
          this.router.navigate(['/']);
        },
        error: error => {
          console.log(error);
          this.empleadoForm.reset();
        }
      });
    }


  }

  esEditar(){
    if(this.id !== null){
      this.titulo = "Editar Empleado";
      this._empleadoService.obtenerEmpleado(this.id).subscribe({
        next: data => {
          this.empleadoForm.setValue({
            nombre: data.nombre,
            apellido: data.apellido, 
            kilos: data.kilos, 
            descuentos: data.descuentos,
            pago: data.pago
          })
        },
        error: error => {
          console.log(error);
        }
      })
    }
  }
}
