import { Component } from '@angular/core';
import { FormGroup,FormBuilder,Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import {Login} from 'src/app/models/login';
import { LoginService } from 'src/app/services/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class CrearProductoComponent {
  loginForm: FormGroup
  titulo=" Login"
  id:string | null;

  constructor(private fb: FormBuilder, 
    private router: Router,
    private toastr: ToastrService,
    private _loginService:LoginService,
    private arouter: ActivatedRoute
    ){
    this.loginForm = this.fb.group({
      usuario: ['', Validators.required],
      contrasena: ['', Validators.required]
    } )
    this.id = this.arouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void{
    this.esEditar();
  }

  agregarUsuario(){
    const login: Login= {
      usuario: this.loginForm.get('usuario')?.value,
      contrasena: this.loginForm.get('contrasena')?.value
    }

    if(this.id !== null){
      //editar un producto
      this._loginService.editarUsuario(this.id,login).subscribe({
        next: data => {
          this.toastr.info('El producto fue actualizado con exito!', 'Usuario Actualizado!');
          this.router.navigate(['/']);
          this.loginForm.reset();
        },
        error: error => {
          console.log(error);
          this.loginForm.reset();
        }
      });
    }else{
      //creamos el login
      console.log(login)
      this._loginService.obtenerUsuario(usuario).subscribe({
        next: data => {
          this.toastr.success('El producto fue registrado con exito!', 'Producto Registrado!');
          this.router.navigate(['/']);
        },
        error: error => {
          console.log(error);
          this.productoForm.reset();
        }
      });
    }


  }

  esEditar(){
    if(this.id !== null){
      this.titulo = "Editar Producto";
      this._productoService.obtenerProducto(this.id).subscribe({
        next: data => {
          this.productoForm.setValue({
            producto: data.nombre,
            categoria: data.categoria, 
            ubicacion: data.ubicacion, 
            precio: data.precio
          })
        },
        error: error => {
          console.log(error);
        }
      })
    }
  }
}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-6 offset-lg-2">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">
                    </span>
                    <form class="mt-3" [formGroup]="loginForm" (ngSubmit)="obtenerUsuario() ">
                        <div class="mb-3">
                            <label class="float-start mb-2">Usuario:</label>
                           <input type="text" 
                            formControlName="usuario"
                            class="form-control form-control-lm" 
                            placeholder="Usuario">
                            <div class="text-danger" 
                            *ngIf="loginForm.get('usuario')?.hasError('required') && loginForm.get('usuario')?.touched ">
                                 <span>El nombre del usuario  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <label class="float-start mb-2">Contraseña:</label>
                            <input type="text" 
                            formControlName="categoria"
                            class="form-control form-control-lm" 
                            placeholder="Categoria">
                            <div class="text-danger"
                            *ngIf="loginForm.get('categoria')?.hasError('required') && loginForm.get('categoria')?.touched ">
                                <span>La contraseña  <strong>es obligatorio</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <button routerLink="/" class="btn btn-secondary btn-lg float-start">Volver</button>
                            <button [routerLink]="['/login',]" type="submit"  class="btn btn-success  btn-lg float-end">Ingresar</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
